sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var currentDate = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		// If the testdate is before currentDate, return true, else return false
		if(testDate.before(currentDate)){
			return true;
		}
		else{
			return false;
		}
	},

	isDateToday: function(strDate){
		//Create GlideDateTime objects for current date and passed in date
		var currentDate = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);

		//If the testDate is equal to the current date, return true, else return false.
		if(testDate.getLocalDate().equals(currentDate.getLocalDate())){
			return true;
		}
		else{
			return false;
		}
	},

    type: 'NeedItUtils'
};
